const socketUrl = "http://10.20.0.177:8000/"

 class Layout extends Component {
   constructor(props) {
       super(props);
       this.state = {
           socket:null,
           user:null
       };
   }
   componentWillMount(){
    this.initSocket()
}

initSocket = () => {
     const socket = io(socketUrl)
     socket.on('connect', () => { console.log("Connected");})
     this.setState({socket})
}

